import React from 'react';

const Countries = (props) => {
  if (props.loading) return <p>Loading countries...</p>;
  if (!props.loading){
    if (props.countries.length > 10) return <p>Too many matches, specity another filter</p>
    return (
      <div>
        {props.countries.map((c) => {
          return <span key={c.alpha3Code}>
            {c.name} <br/>
          </span>
        })}
      </div>
    )
  }



}

export default Countries;
