import React from 'react';

const Persons = (props) => {

  return (
    <div>
      {props.persons
        .filter((p) => {
          return p.name.toUpperCase().includes(props.newSearch.toUpperCase());
        })
        .map((p) => {
          return (
            <p key={p.id}>
              {p.name} - {p.number}
            </p>
          );
        })}
    </div>
  );
}

export default Persons;
